export const code = async (inputs) => {
  let content = inputs.ai_response || "{}";

  // 1. Remove Markdown code blocks (```json ... ```)
  // This regex replaces ```json, ```, and newlines associated with them
  content = content.replace(/^```json\s*/g, "").replace(/^```\s*/g, "").replace(/\s*```$/g, "");

  // 2. Attempt to find the JSON object if there is extra text
  // This looks for the first '{' and the last '}'
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    content = jsonMatch[0];
  }

  try {
    // 3. Parse the cleaned string into a JSON object
    return JSON.parse(content);
  } catch (error) {
    // Fallback if parsing fails
    console.error("Failed to parse JSON:", error);
    return {
      title: "Error Parsing AI Response",
      content: inputs.ai_response, // Return raw text so you don't lose it
      theme: "error"
    };
  }
};

export const code = async (inputs) => {
  return {
    postMeta: {
      recentPosts: inputs.recentPosts,
      dailyDiff: inputs.dailyDiff
    }
  };
};

export const code = async (inputs) => {
  const item = inputs.item || {};
  const cells = item.cells || {};

  // Helper function to find value by field name
  const getCellValue = (fieldName) => {
    // Iterate through all cell objects to match the fieldName
    const cell = Object.values(cells).find(c => c.fieldName === fieldName);
    return cell ? cell.value : null;
  };

  return {
    platformMeta: {
      platform: getCellValue("Platform") || 'Unknown',
      audience: getCellValue("Audience") || 'General',
      intention: getCellValue("Intention") || 'Value',
      // Convert these to numbers since they come in as strings
      charLimit: Number(getCellValue("Char Limit")) || 0,
      recentPostLimit: Number(getCellValue("Recent Post Limit")) || 5
    }
  };
};

export const code = async (inputs) => {
  return {
    // Primary Data
    draftInfo: inputs.draftInfo,
    platform: inputs.platform,

    // Context for AI rewriting
    dailyDiff: inputs.dailyDiff,
    recentPosts: inputs.recentPosts,

    // recursion control
    tries: 1,           // Start with 1st try
    feedback: null,     // No feedback for a fresh draft
    lastDraft: null     // No rejected draft yet
  };
};

