export const code = async (inputs) => {
  const data = inputs.rawData;

  // Helper to safely parse JSON strings or return empty objects
  const safeParse = (val) => {
    try {
      return typeof val === 'string' ? JSON.parse(val) : val || {};
    } catch (e) {
      return {};
    }
  };

  const context = safeParse(data.postContext);
  const drafts = safeParse(data.drafts);
  const platformData = safeParse(data.platformData);
  const feedback = safeParse(data.feedback);

  return {
    // Existing logic
    dailyDiff: context.dailyDiff || '',
    recentPosts: JSON.stringify(context.recentPosts || []),
    lastDraft: drafts.length > 0 ? JSON.stringify(drafts[drafts.length - 1]) : null,

    // New: Pass parsed objects directly
    platform: platformData,
    feedback: feedback
  };
};

export const code = async (inputs) => {
  let content = inputs.ai_response || "{}";

  // 1. Remove Markdown code blocks (```json ... ```)
  // This regex replaces ```json, ```, and newlines associated with them
  content = content.replace(/^```json\s*/g, "").replace(/^```\s*/g, "").replace(/\s*```$/g, "");

  // 2. Attempt to find the JSON object if there is extra text
  // This looks for the first '{' and the last '}'
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    content = jsonMatch[0];
  }

  try {
    // 3. Parse the cleaned string into a JSON object
    return JSON.parse(content);
  } catch (error) {
    // Fallback if parsing fails
    console.error("Failed to parse JSON:", error);
    return {
      title: "Error Parsing AI Response",
      content: inputs.ai_response, // Return raw text so you don't lose it
      theme: "error"
    };
  }
};

