export const code = async (inputs) => {
  const currentStr = inputs.currentDrafts;
  const newDraftInput = inputs.generatedDraft;

  // 1. Initialize array (Handle both Array and Single Object)
  let draftsArray = [];
  try {
    if (currentStr) {
      const parsed = typeof currentStr === 'string' ? JSON.parse(currentStr) : currentStr;

      if (Array.isArray(parsed)) {
        draftsArray = parsed;
      } else if (typeof parsed === 'object' && parsed !== null) {
        // If it's a single draft object, wrap it in an array
        draftsArray = [parsed];
      }
    }
  } catch (e) {
    draftsArray = []; 
  }

  // 2. Parse the new draft
  let newDraftObj = null;
  try {
    newDraftObj = typeof newDraftInput === 'string' ? JSON.parse(newDraftInput) : newDraftInput;
  } catch (e) {
    newDraftObj = { content: newDraftInput, theme: "Unknown", title: "Draft" };
  }

  // 3. Append and return
  if (newDraftObj) {
    draftsArray.push(newDraftObj);
  }

  return {
    updatedDrafts: JSON.stringify(draftsArray)
  };
};
